import React, { useState } from "react";
import WeatherInfo from "./WeatherInfo";
import WeatherForecast from "./WeatherForecast";
import axios from "axios";
import "./Weather.css";
export default function Weather(props) {
  const [weatherData, setWeatherData] = useState({ ready: false });
  const [city, setCity] = useState(props.defaultCity);

  function handleResponse(response) {
    setWeatherData({
      ready: true,
      coordinates: response.data.coord,
      temperature: response.data.main.temp,
      humidity: response.data.main.humidity,
      date: new Date(response.data.dt * 1000),
      description: response.data.weather[0].description,
      icon: response.data.weather[0].icon,
      wind: response.data.wind.speed,
      city: response.data.name,
    });
  }

  function handleSubmit(event) {
    event.preventDefault();
    search();
  }

  function handleCityChange(event) {
    setCity(event.target.value);
  }

  function search() {
    const apiKey = "5f472b7acba333cd8a035ea85a0d4d4c";
    let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
    axios.get(apiUrl).then(handleResponse);
  }

  if (weatherData.ready) {
    return (
      <div className="Weather">
        <div class="search">
          <form
            id="searchForm"
            class="row justify-content-md-center"
            onSubmit={handleSubmit}
          >
            <div class="col-auto">
              <input
                type="Search"
                placeholder="Search the city"
                id="search-text-input"
                onChange={handleCityChange}
              />
            </div>
            <div class="col-auto">
              <button class="btnSearch">Search</button>
            </div>
          </form>
        </div>
        <WeatherInfo data={weatherData} />
        <WeatherForecast coordinates={weatherData.coordinates} />
        <div class="currentDate">
          Last updated: <span id="currentDate">Date</span>
        </div>
        <div class="clearfix weather-temperature">
          <img
            src="https://ssl.gstatic.com/onebox/weather/64/sunny.png"
            alt="Clear"
            id="icon"
            class="currentIcon"
          />
        </div>
        <div class="weatherDescription" id="weatherDescription">
          Weather description
        </div>
        <div class="mainCity" id="searchCity">
          City
        </div>
        <div class="currentTemperature" id="temperature">
          24
        </div>
        <div class="units">°C</div>

        <div class="humidityDisplay">
          Humidity: <span id="humidity">12</span>%
        </div>
        <div class="windDisplay">
          Wind: <span id="wind">12</span> m/h
        </div>
        <div class="weather-forecast" id="forecast"></div>
        <button class="btnCurrent" id="current-location-button">
          Weather in current location
        </button>
      </div>
    );
  } else {
    search();
    return "Loading...";
  }
}
